import { useState } from "react";
import Icon from "react-native-vector-icons/FontAwesome";

import { useContext } from "react/cjs/react.development";
import { BackgroundLinear } from "../Home/gradient";
import { useNavigation } from "@react-navigation/native";
import InputComponent from "../../components/input/index";
import ButtonComponent from "../../components/button/index";
import { AuthContex } from "../../contexts/auth";
import { database } from "../../config/firebase";
import { collection, addDoc } from "firebase/firestore";

import {
  Container,
  ImageComponet,
  TextComponet,
  TitleComponet,
  Button
} from "./styles";

const Login = () => {
  const userCollection = collection(database, "User");


  const navigation = useNavigation();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    if (email && password) {
     await addDoc(userCollection, {
        email: email,
        password: password,
        
      });
      alert("Agenda Cadastrada!");
      navigation.navigate("Home");
    } else {
      alert("Preencha todos os campos!");
    }
  };

  return (
    <BackgroundLinear>
      <Container>
      <Button onPress={() => navigation.navigate("Main")}>
          <Icon name="arrow-left" size={30} color="#fff"  />
          <TextComponet m={'10px'} mb={'0'}>Voltar </TextComponet>
        </Button>
        
        <ImageComponet source={require("../../assets/logo.png")} />
        <TitleComponet>Barbearia Rei Do Corte</TitleComponet>
        <TextComponet>Já é cliente ? Faça Login</TextComponet>
   
        <InputComponent
        
          onChangeText={(value) => setEmail(value)}
          type="text"
          label="Email"
          placeholder="email@email.com"
        ></InputComponent>

        <InputComponent
        
          onChangeText={(value) => setPassword(value)}
          type="password"
          label="Senha"
          placeholder="******"
        ></InputComponent>
      
        <ButtonComponent onPress={handleLogin} text="Entrar"></ButtonComponent>
      </Container>
    </BackgroundLinear>
  );
};

export default Login;
