import { useState, useEffect } from "react";
import Icon from "react-native-vector-icons/FontAwesome";
import { BackgroundLinear } from "../Home/gradient";
import { useNavigation } from "@react-navigation/native";
import InputComponent from "../../components/input/index";
import ButtonComponent from "../../components/button/index";
import { database } from "../../config/firebase";
import { collection, addDoc, getDocs } from "firebase/firestore";

import {
  Container,
  ImageComponet,
  TextComponet,
  TitleComponet,
  Button,
} from "./styles";

const Login = () => {
  const userCollection = collection(database, "User");

  const navigation = useNavigation();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [tasks, setTasks] = useState([]);

  const handleLogin = async () => {
    if (email && password) {
      await addDoc(userCollection, {
        email: email,
        password: password,
      });
      alert("Agenda Cadastrada!");
      navigation.navigate("Home");
    } else {
      alert("Preencha todos os campos!");
    }
  };

  useEffect(() => {
    const getUser = async () => {
      const data = await getDocs(userCollection);
      console.log(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
      const userData = data.docs.map((doc) => ({ ...doc.data(), id: doc.id }))

      if(userData){
        userData.map((e) => {
          setEmail(e.email);
          setPassword(e.password);
        });
      }
      setTasks(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
    };
    getUser();
  }, []);
 
  

  return (
    <BackgroundLinear>
      <Container>
        <Button onPress={() => navigation.navigate("Main")}>
          <Icon name="arrow-left" size={30} color="#fff" />
          <TextComponet m={"10px"} mb={"0"}>
            Voltar{" "}
          </TextComponet>
        </Button>

        <ImageComponet source={require("../../assets/logo.png")} />
        <TitleComponet>Barbearia Rei Do Corte</TitleComponet>
        <TextComponet>Já é cliente ? Faça Login{email}{}</TextComponet>

        <InputComponent
          value={email}
          onChangeText={(value) => setEmail(value)}
          type="text"
          label="Email"
          placeholder="email@email.com"
        ></InputComponent>

        <InputComponent
          value={password}
          onChangeText={(value) => setPassword(value)}
          type="password"
          label="Senha"
          placeholder="******"
        ></InputComponent>

        <ButtonComponent onPress={handleLogin} text="Entrar"></ButtonComponent>
      </Container>
    </BackgroundLinear>
  );
};

export default Login;
